import { useState, useEffect, createContext, useContext, useCallback } from 'react'
import Image from 'next/image'

import {
  useAccount,
  useConnect,
  useDisconnect,
  useEnsName,
  useProvider,
} from 'wagmi'
import { signMessage } from 'wagmi/actions'

import { useRings, Chain, UnsignedInfo } from '@ringsnetwork/rings-provider'

import { useWallet } from '../contexts/SolanaWalletProvider'
import formatAddress from '../utils/formatAddress';
import { ADDRESS_TYPE } from '../utils/const';

import {
  Dialog,
  DialogContent,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"

import PhantomCard from '../components/connectors/PhantomCard'

// Convert a hex string to a byte array
export function hexToBytes(hex: number | string) {
  hex = hex.toString(16)

  hex = hex.replace(/^0x/i, '')

  for (var bytes = [], c = 0; c < hex.length; c += 2) bytes.push(parseInt(hex.slice(c, c + 2), 16))
  return bytes
}

// Convert a byte array to a hex string
export function bytesToHex(bytes: number[]) {
  let hex = []
  for (let i = 0; i < bytes.length; i++) {
    let current = bytes[i] < 0 ? bytes[i] + 256 : bytes[i]
    hex.push((current >>> 4).toString(16))
    hex.push((current & 0xf).toString(16))
  }
  return hex.join('')
}

interface MultiWeb3Context {
  account: string,
  accountName: string,
  signature: Uint8Array | null,
  chain: string,
  unsignedInfo: UnsignedInfo | null,
  provider: any,
  addressType: ADDRESS_TYPE,
  open: boolean,
  setOpen: () => void
}

export const MultiWeb3Context = createContext<MultiWeb3Context>({
  account: '',
  accountName: '',
  signature: null,
  chain: '',
  unsignedInfo: null,
  provider: null,
  addressType: ADDRESS_TYPE.DEFAULT,
  open: false,
  setOpen: () => {}
})

export const useMultiWeb3 = () => useContext(MultiWeb3Context)

const MultiWeb3Provider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // const { account: ethereumAccount, provider } = useWeb3React()
  // const name = useENS()

  const provider = useProvider()
  const { address } = useAccount()
  const { data: ensName } = useEnsName({ address })
  const { connect, connectors } = useConnect()

  const { wallet, connected } = useWallet()

  const { createUnsignedInfo } = useRings()

  const [account, setAccount] = useState('')
  const [chain, setChain] = useState('')
  const [signature, setSignature] = useState<Uint8Array | null>(null)
  const [unsignedInfo, setUnsignedInfo] = useState<UnsignedInfo | null>(null)
  const [accountName, setAccountName] = useState('')
  const [addressType, setAddressType] = useState(ADDRESS_TYPE.DEFAULT)

  const [open, setOpen] = useState(false)

  const handleShowDialog = useCallback(() => {
    setOpen((prev) => !prev)
  }, [setOpen])

  useEffect(() => {
    if (address && ensName) {
      setAccountName(ensName)
    }
  }, [ensName, address])


  useEffect(() => {
    // console.group('multi')
    //   console.log(`ethereumAccount:`, address);
    //   console.log(`connected`, connected)
    //   console.log(`wallet`, wallet?.publicKey?.toBase58())
    // console.groupEnd()
    if (address && provider) {
      setChain('ethereum')
      setAccount(address.toLowerCase())
      setAccountName(formatAddress(address))

      const getEthereumSignature = async () => {
        const unsignedInfo = createUnsignedInfo({ address })
        const signed = await signMessage({
          message: unsignedInfo.auth,
        })
        const signature = new Uint8Array(hexToBytes(signed))
  
        setUnsignedInfo(unsignedInfo)
        setSignature(signature)
      }

      getEthereumSignature()
    } else if (connected && wallet && wallet.publicKey) {
      const pubKey = wallet.publicKey.toBase58()
      setChain('solana')
      setAccount(pubKey)
      setAccountName(formatAddress(pubKey))
      setAddressType(ADDRESS_TYPE.ED25519)

      const getSolanaSignature = async () => {
        const unsignedInfo =createUnsignedInfo({ address: pubKey, chain: Chain.SOLANA })
        const data = new TextEncoder().encode(unsignedInfo.auth);
        const signature = await wallet.signMessage(data, 'utf8');
  
        setUnsignedInfo(unsignedInfo)
        setSignature(signature)
      }

      getSolanaSignature()
    } else {
      setChain('')
      setAccount('')
      setSignature(null)
      setAddressType(ADDRESS_TYPE.DEFAULT)
    }
  }, [address, wallet, connected, provider])

  return (
    <MultiWeb3Context.Provider
      value={{
        account,
        accountName,
        signature,
        chain,
        unsignedInfo,
        provider,
        addressType,
        open,
        setOpen: handleShowDialog,
      }}
    >
      { children }

      <Dialog open={open} onOpenChange={handleShowDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Select a wallet provider</DialogTitle>
          </DialogHeader>

          <Tabs defaultValue="ethereum">
            <TabsList>
              <TabsTrigger value="ethereum">Ethereum</TabsTrigger>
              <TabsTrigger value="solana">Solana</TabsTrigger>
            </TabsList>

            <TabsContent value="ethereum">
              {
                connectors.map((connector) => {
                  return (
                    <div
                      key={connector.id}
                      className='flex items-center flex-grow-0 w-full mb-4 cursor-pointer' 
                      onClick={() => {
                        connect({ connector })
                        handleShowDialog()
                      }}
                    >
                      <div className='mr-5 w-11 h-11'>
                        {
                          connector.id === 'metaMask' ?
                          <Image alt="Icon" src="data:image/svg+xml;base64," width={45} height={45} /> :
                          null
                        }
                        {
                          connector.id === 'walletConnect' ?
                          <Image alt="" src="data:image/svg+xml;base64," width={45} height={45} /> :
                          null
                        }
                      </div> 
                      <div className='text-sm font-bold'>{connector.name}</div>
                    </div>
                  )
                }) 
              }
            </TabsContent>

            <TabsContent value="solana">
              <PhantomCard onConnect={handleShowDialog} />
            </TabsContent>

          </Tabs>

        </DialogContent>
      </Dialog>
    </MultiWeb3Context.Provider>
  )
}

export default MultiWeb3Provider